from rest_framework import serializersfrom shop.models import *class BrandSerializer(serializers.ModelSerializer):    class Meta:        model = Brand        fields = [            'BrandName',            'Img'        ]class PetTypeSerializer(serializers.ModelSerializer):    class Meta:        model = PetType        fields = [            'TypeName',            'Description'        ]class CategoryProductSerializer(serializers.ModelSerializer):    class Meta:        model = CategoryProduct        fields = [            'CategoryName',            'PetType'        ]class CatalogProductSerializer(serializers.ModelSerializer):    class Meta:        model = CatalogProduct        fields = [            'ProductName',            'PriceOfProduct',            'DescriptionProduct',            'Img',            'Quantity',            'Brand',            'Category'        ]class PromotionSerializer(serializers.ModelSerializer):    class Meta:        model = Promotion        fields = [            'PromotionName',            'DiscountPercentage',            'StartDate',            'EndDate',            'CatalogProduct'        ]class OrderSerializer(serializers.ModelSerializer):    class Meta:        model = Order        fields = [            'buyer_surname',            'buyer_name',            'buyer_middlename',            'comment',            'delivery_address',            'delivery_type',            'date_create',            'date_finish',            'catalogproduct'        ]class Pos_orderSerializer(serializers.ModelSerializer):    class Meta:        model = Pos_order        fields = [            'catalogproduct',            'order',            'count',            'discount'        ]