from django import formsfrom django.contrib.auth.forms import UserCreationForm, AuthenticationFormfrom django.contrib.auth.models import Userfrom .models import  Brand, CategoryProduct, PetType, Promotion, CatalogProductclass BrandsForm(forms.ModelForm):    class Meta:        model = Brand        fields = ['BrandName', 'Img']class CategoryProductForm(forms.ModelForm):    class Meta:        model = CategoryProduct        fields = ['CategoryName', 'PetType']class PetTypeForm(forms.ModelForm):    class Meta:        model = PetType        fields = ['TypeName', 'Description']class PromotionForm(forms.ModelForm):    class Meta:        model = Promotion        fields = ['PromotionName', 'DiscountPercentage', 'StartDate', 'EndDate', 'CatalogProduct']class CatalogProductForm(forms.ModelForm):    class Meta:        model = CatalogProduct        fields = ['ProductName', 'PriceOfProduct', 'DescriptionProduct', 'Img', 'Quantity', 'Brand', 'Category']class RegistrationForm(UserCreationForm):    username = forms.CharField(        label='Логин пользователя',        widget=forms.TextInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        }),        min_length=2    )    email = forms.CharField(        label='Электронная почта',        widget=forms.EmailInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        })    )    password1 = forms.CharField(        label='Придумайте пароль',        widget=forms.PasswordInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        })    )    password2 = forms.CharField(        label='Повторите пароль',        widget=forms.PasswordInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        })    )    class Meta:        model = User        fields = ['username', 'email', 'password1', 'password2']class LoginForm(AuthenticationForm):    username = forms.CharField(        label='Логин пользователя',        widget=forms.TextInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        })    )    password = forms.CharField(        label='Введите пароль',        widget=forms.PasswordInput(attrs={            'class': 'w-full p-3 rounded-md bg-white text-black border border-orange-500 focus:border-orange-400 focus:ring focus:ring-orange-400 focus:ring-opacity-50',            'style': 'height: 2.75rem; box-sizing: border-box;'        })    )